import React,{useState} from 'react'

export default function TextForm(props) {
  const handleUpClickUpper = ()=>{
    // let newText = text.toUpperCase();
    setText(text.toUpperCase())
  }
   const handleUpClickLower = ()=>{
    let newText = text.toLowerCase();
    setText(newText)
  }
  const handleUpClickClear = () =>{
    let newText = "";
    setText(newText);
  } 
  const handleOnChange =(event)=>{
    let newst = event.target.value;
    // setText(event.target.value);
    setText(newst);
  }
  const handleCopyText =()=>{
    let text = document.getElementById('myBox')
    text.select();
    navigator.clipboard.writeText(text.value);  
  }
    const [text,setText] = useState("Convert");
    const [Copy, setCopy] = useState("Copy")
    // text = "So this is wrong way to change . . .";
    // setText  = "So wrong way . . ";
  return (
    <div>
        <div className="container">
          <div className="row mt-5">
      <div className="col-md-5">
        <label htmlFor="myBox"><h4 className='text-primary'>{props.enterDHeading}</h4></label>
        <textarea name="" className='form-control bg-light' onChange={handleOnChange}  id="myBox" cols="" rows="8" placeholder='Enter the Text to Convert'></textarea>
      </div>
      <div className="col-md-2 mt-md-5">
        <div className="row">
          <div className="col-3 mt-2 mt-md-0 col-md-6">
      <button className="btn btn-dark" onClick={handleUpClickUpper}>Upper</button>
          </div>
          <div className="col-3 mt-2 mt-md-0 col-md-6">
      <button className="btn btn-dark" onClick={handleUpClickLower}>Lower</button>
          </div>
          <div className="col-3 col-md-6 mt-2 mt-md-3">
      <button className="btn btn-warning" onClick={handleUpClickClear}>Clear</button>
          </div>
          <div className="col-3 col-md-6 mt-2 mt-md-3">
      <button className="btn btn-warning" onClick={handleUpClickLower}>Lower</button>

          </div>
        </div>
      </div>
      <div className="col-md-5">
        <label htmlFor="myBox"><h4 className='text-primary'>{props.convertDHeading}</h4></label>
        <textarea name="" className='form-control'  value={text} id="myBox" cols="" rows="8" disabled></textarea>
      </div>
          </div>
          <div className="row">
            <div className="col-md-6">
              <h2>Your Text Summary</h2>
              <p>You have<span className='badge badge-dark text-dark'> {text.split(" ").length} words </span> and <span className='badge badge-dark text-dark badge-pill'> {text.length}</span> Charactors.</p>
            </div>
          </div>
    </div>
        </div>
  )
}
