import React from 'react'

export default function textForm(props) {
  return (
    <div>
        <div className="container">
      <div className="mb-3">
        <label htmlFor="myBox"><h3>{props.heading}</h3></label>
        <textarea name="" className='form-control' id="myBox" cols="" rows="5"></textarea>
      </div>
    </div>
        </div>
  )
}
