import React, { useState } from "react";
// import React from "react";
import "./App.css";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import Alert from "./components/Alert";
import About from "./components/About";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
function App() {
  const [mode, setMode] = useState('light');
  const [alertd, setAlert] = useState(null)
  const [dSwitch, setSwitch] = useState({
    background: '#ffa',
    border:'2px solid white',
    color: 'white'
  })
  const showAlert = (message, type)=>{
    setAlert({
      message: message,
      type: type, 
    })
  }
  setTimeout(()=>{
    setAlert(null);
  }, 2500);
  const handleMode = () =>{
    if(mode === 'light'){
      setMode('dark')
      document.body.style.backgroundColor = '#626466';
      document.body.style.color = 'white'
      showAlert("Dark Mode has been enable.", "success");
      setSwitch({
        background: '#33404ce3',
        border:'2px solid white',
        color: 'white'
      })
        document.title = "Dark Mode Enable"
      // setInterval(()=>{
      //   document.title = "Dark Mode has been On"
      // },2000)
      // setInterval(()=>{
      //   document.title = "You Wanna Off dark mode"
      // },2500)
    }
    else{
      setMode('light')
      showAlert("Light Mode has been enable.", "success");
        document.title = "Light Mode Enable"
      document.body.style.backgroundColor = 'white';
      document.body.style.color = 'black'
      setSwitch({
        background: 'white',
        border:'2px solid white',
        color: 'black'
      })
    }
  }

  return <Router>
  <Navbar title="Hello" mode = {mode} enableMode={handleMode} switchBg= {dSwitch}/>
  <Alert alert= {alertd}/>
  <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/">
          <TextForm enterDHeading="Enter Text to analyze" 
            mode = {mode} 
            convertDHeading="Converted Text" 
            alert={showAlert}
            />
          </Route>
        </Switch>
        </Router>
}

export default App;
