import React, { useState } from 'react'
// import React from 'react'
export default function Model(props) {
    const [copied, setCopied] = useState("Copy")
    const [text,setText] = useState("Convert this text");

    const Upper = ()=>{
        // let newText = text.toUpperCase();
        setText(text.toUpperCase())
        props.alert("Your Text have Converted to Upper Case","success")
      }
       const Lower = ()=>{
        let newText = text.toLowerCase();
        setText(newText);
        props.alert("Your Text have Converted to Lower Case", "success");
      }
      const Clear = () =>{
        let Atext = document.getElementById("myBox");
        Atext.value = "";
        let newText = "";
        setText(newText);
        props.alert("The Text succesfuly is clear","success")
      } 
      const OnChange =(event)=>{
        let newst = event.target.value;
        // setText(event.target.value);
        setText(newst);
      }
    const handleCopiedText =()=>{
        let text1 = document.getElementById('recipient-name')
        let text2 = document.getElementById('message-text')
        text1.select();
        text2.select();
        navigator.clipboard.writeText(text1.value +" "+ text2.value);
        // navigator.clipboard.writeText(text2.value);
        setCopied("Copied!");
          // props.alert("Your Text is succesfuly copied to your clipboard","success")
          setTimeout(()=>{
            setCopied("Copy");
          }, 4500)
      }
  return (
    <div>
        <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
            <div className={`modal-content bg-${props.mode} text-${props.mode === 'light'?'dark':'light'}`}>
            <div className="modal-header">
                <h1 className="modal-title fs-5" id="exampleModalLabel">New message</h1>
                <button type="button" className="btn-close bg-light text-darks" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className={`modal-body bg-${props.mode} text-${props.mode === 'light'?'dark':'light'}`}>
                <form>
                <div className="mb-1">
                    <label for="recipient-name" className="col-form-label">Your Text:</label>
                    <textarea type="text" className="form-control" id="recipient-name" value={props.modelText} placeholder='Nothing is copied yet'/>
                </div>
                <div className="mb-3">
                    <label for="message-text" className="col-form-label">Add:</label>
                    <textarea className="form-control" id="message-text" cols="40" rows="10" placeholder='Enter the New Text' onChange={OnChange}></textarea>
                </div>
                </form>
            </div>
            <div className="modal-footer">
                <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={handleCopiedText}>{copied}</button>
                <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={Upper}>Upper</button>
                <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={Lower}>Lower</button>
                <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={Clear}> Clear </button>

                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                {/* <button type="button" className="btn btn-primary">Send message</button> */}
            </div>
            </div>
        </div>
        </div>
    </div>
  )
}
