import React,{useState} from 'react'

export default function TextForm(props) {
  const [removeSpace, setRemoveSpace] = useState("RSpace")
  const handleUpClickUpper = ()=>{
    // let newText = text.toUpperCase();
    setText(text.toUpperCase())
    props.alert("Your Text have Converted to Upper Case","success")
  }
   const handleUpClickLower = ()=>{
    let newText = text.toLowerCase();
    setText(newText);
    props.alert("Your Text have Converted to Lower Case", "success");
  }
  const handleUpClickClear = () =>{
    let Atext = document.getElementById("myBox");
    Atext.value = "";
    let newText = "";
    setText(newText);
    props.alert("The Text succesfuly is clear","success")
  } 
  const handleOnChange =(event)=>{
    let newst = event.target.value;
    // setText(event.target.value);
    setText(newst);
  }
  const handleCopyText =()=>{
    let text = document.getElementById('myBox')
    text.select();
    navigator.clipboard.writeText(text.value);  
    setCopy("Copied!");
      setSty({
        background:'#fff',
        color:'#000',
        border:'1px solid #000f'
      })
      props.alert("Your Text is succesfuly copied to your clipboard","success")
  }
  setTimeout(()=>{
    setCopy("Copy");
  }, 4500)
  const handleRemoveSpace = ()=>{
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "))
    setRemoveSpace("Removed Spaces!")
    setTimeout(()=>{
      setRemoveSpace("RSpace");
    }, 4500)
    props.alert("Removed extra spaeses from your text ","success")
  }
    const [text,setText] = useState("Convert");
    const [copy, setCopy] = useState("Copy");
    const [sty, setSty] = useState({
      background: '#fff',
      color: '#000'
    });

    // text = "So this is wrong way to change . . .";
    // setText  = "So wrong way . . ";
  return (
    <div>
        <div className="container">
          <div className="row mt-3">
      <div className="col-md-5">
        <label htmlFor="myBox"><h4 className={`text-${props.mode === 'dark'?'light':'dark'}`}>{props.enterDHeading}</h4></label>
        <textarea name="" className={`text-${props.mode==='dark'?'light':'dark'} form-control bg-${props.mode} `} onChange={handleOnChange}  id="myBox" cols="" rows="8" placeholder='Enter the Text to Convert'></textarea>
      </div>
      <div className="col-md-2 mt-md-5">
        <div className="row">
          <div className="col-3 mt-2 mt-md-0 col-md-6">
      <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={handleUpClickUpper}>Upper</button>
          </div>
          <div className="col-3 mt-2 mt-md-0 col-md-6">
      <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={handleUpClickLower}>Lower</button>
          </div>
          <div className="col-3 col-md-6 mt-2 mt-md-3">
      <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={handleUpClickClear}> Clear </button>
          </div>
          <div className="col-3 col-md-6 mt-2 mt-md-3">
      <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} style={sty} onClick={handleCopyText}>{copy}</button>
          </div>
          <div className="col-3 col-md-6 mt-2 mt-md-3">
      <button className={`btn btn-${props.mode === 'dark'?'light':'dark'}`} onClick={handleRemoveSpace}>{removeSpace}</button>
          </div>
        </div>
      </div>
      <div className="col-md-5">
        <label htmlFor="myBox"><h4 className={`text-${props.mode === 'dark'?'light':'dark'}`}>{props.convertDHeading}</h4></label>
        <textarea name="" className={`bg-${props.mode} border border-0 text-${props.mode==='dark'?'light':'dark'} form-control `}  value={text} id="myBox" cols="" rows="8" disabled></textarea>
      </div>
          </div>
          <div className="row">
            <div className="col-md-6">
              <h2>Your Text Summary</h2>
              <p>You have<span className='badge badge-dark text-dark'> {text.split(" ").length} words </span> and <span className='badge badge-dark text-dark badge-pill'> {text.length}</span> Charactors.</p>
            </div>
          </div>
    </div>
        </div>
  )
}
