import "./App.css";
import React, { useState } from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import Alert from "./components/Alert";
// import About from "./components/About";
function App() {
  const [mode, setMode] = useState('light');
  const [alertd, setAlert] = useState(null)
  const [dSwitch, setSwitch] = useState({
    background: '#ffa',
    border:'2px solid white',
    color: 'white'
  })
  const showAlert = (message, type)=>{
    setAlert({
      message: message,
      type: type, 
    })
  }
  setTimeout(()=>{
    setAlert(null);
  }, 1500);
  const handleMode = () =>{
    if(mode === 'light'){
      setMode('dark')
      document.body.style.backgroundColor = '#626466';
      document.body.style.color = 'white'
      showAlert("Dark Mode has been enable.", "success");
      setSwitch({
        background: '#33404ce3',
        border:'2px solid white',
        color: 'white'
      })
    }
    else{
      setMode('light')
      showAlert("Light Mode has been enable.", "success");
      document.body.style.backgroundColor = 'white';
      document.body.style.color = 'black'
      setSwitch({
        background: 'white',
        border:'2px solid white',
        color: 'black'
      })
    }
  }

  return <>
  <Navbar title="Hello" mode = {mode} enableMode={handleMode} switchBg= {dSwitch}/>
  <Alert alert= {alertd}/>
  <TextForm enterDHeading="Enter Text to analyze" mode = {mode} convertDHeading="Converted Text" alert={alertd}/>
  {/* <About/> */}
  </>;
}

export default App;
