import React, { Component } from 'react'
export default  function alert(props){
    const cap= (word)=>{
        const lower = word.toLowerCase();
        return lower.charAt(0).toUpperCase() + word.slice(1);
    }
    const newLocal = props.alert
    return( newLocal && <div>
     <div className="container position-relative">
        <div className="row">
                <div className={`position-absolute top-0 start-0 opacity-50 alert alert-${props.alert.type}`} role="alert">
                <strong>{cap(props.alert.type)}</strong> : {props.alert.message}
                </div>
        </div>
    </div>
</div>
    )
}
