export default function Model(props) {
    const [UpdateText, SetUpdateText] = useState(props.text)
    const handleCopyText =()=>{
        let ntext = document.getElementById('myBox')
        ntext.select();
        navigator.clipboard.writeText(ntext.value); 
        SetUpdateText(ntext); 
        setCopy("Copied!");
          props.alert("Your Text is succesfuly copied to your clipboard","success")
          setTimeout(()=>{
            setCopy("Copy");
          }, 4500)
      }
  return (
    <div>
