import "./App.css";
// import React from "react";
import React, { useState } from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import About from "./components/About";
import {BrowserRouter as Router, Route, Link, Routes} from 'react-router-dom';
export default App;
 function App(): JSX.Element {
  const [mode, setMode] = useState('light');
  const [dSwitch, setSwitch] = useState({
    background: '#ffa',
    border:'2px solid white',
    color: 'white'
  })
  const handleMode = () =>{
    if(mode === 'light'){
      setMode('dark')
      document.body.style.backgroundColor = '#626466';
      document.body.style.color = 'white'
      setSwitch({
        background: '#33404ce3',
        border:'2px solid white',
        color: 'white'
      })
        document.title = "Dark Mode Enable"
      // setInterval(()=>{
      //   document.title = "Dark Mode has been On"
      // },2000)
      // setInterval(()=>{
      //   document.title = "You Wanna Off dark mode"
      // },2500)
    }
    else{
      setMode('light')
      document.title = "Light Mode Enable"
      document.body.style.backgroundColor = 'white';
      document.body.style.color = 'black'
      setSwitch({
        background: 'white',
        border:'2px solid white',
        color: 'black'
      })
    }
  }
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
          </ul>
        </nav>

        {/* üëáÔ∏è Wrap your Route components in a Routes component */}
        <Routes>
          <Route path="/about" element={<About />} />
          <Route path="/" element={<TextForm />} />
        </Routes>
      </div>
    </Router>
  );
}

