    <div>
        <div className="container">
          <div className="row mb-3">
      <div className="col-md-5">
        <label htmlFor="myBox"><h4 className='text-primary'>{props.heading}</h4></label>
        <textarea name="" className='form-control bg-light' onChange={handleOnChange} id="myBox" cols="" rows="8" placeholder='Enter the Text to convert'></textarea>
